INSERT INTO `sys_oper_log` VALUES (331, 'word上传', 1, 'com.ruoyi.system.controller.SysWordInfoController.add()', 'POST', 1, 'admin', NULL, '/system/word', '127.0.0.1', '内网IP', '{\"fileId\":17,\"fileRows\":4,\"fileWord\":\"\\n预留标准对接接口，根据上级要求及项目建设过程中的实际情况，随时增加系统的对接整合。\\n6、与市场监管信息平台对接\\n预留标准对接接口，根据上级要求及项目建设过程中的实际情况，随时增加系统的对接整合。\\n\\n\",\"params\":{}}', '{\"msg\":\"操作成功\",\"code\":200}', 0, NULL, '2023-04-04 22:49:57', 113);
